CREATE PROCEDURE [dbo].[usp_InsertCustomer]
(
	@CustomerName VARCHAR(500),
    @FirstName VARCHAR(200),
    @LastName VARCHAR(300),
    @Gender VARCHAR(10),
    @Incomegroup VARCHAR(500),
    @Region VARCHAR(500),
    @Maritalstatus VARCHAR(500)
)
AS

BEGIN
	SET NOCOUNT ON;

	BEGIN
		    INSERT INTO Policy (PolicyCode,PurchaseDate, Fuel, VehicleSegment, Premium,bodily_injury_liability, personal_injury_protection,property_damage_liability,collision,comprehensive)
            VALUES (@PolicyCode, @PurchaseDate, @Fuel, @VECHILE_SEGMENT, @Premium,@InjuryLiability,@InjuryProtection,@ProperyLiability,@Collision,@Comprehensive);
	END

	SELECT SCOPE_IDENTITY();
END

CREATE PROCEDURE [dbo].[usp_InsertPolicy]
(
    @PolicyCode VARCHAR(500),
	@PurchaseDate DATETIME,
	@Fuel VARCHAR(500) = NULL,
	@VECHILE_SEGMENT  VARCHAR(500),
	@Premium DECIMAL(10, 2),
	@InjuryLiability DECIMAL(10, 2) = NULL,
    @InjuryProtection DECIMAL(10, 2) = NULL,
    @ProperyLiability DECIMAL(10, 2) = NULL,
    @Collision DECIMAL(10, 2) = NULL,
    @Comprehensive DECIMAL(10, 2) = NULL
)
AS

BEGIN
	SET NOCOUNT ON;

	BEGIN
		    INSERT INTO Policy_Customer (PolicyId, CustomerId)
            VALUES (@PolicyId, @CustomerId);
	END

    
	SET @AffectedRows = @@ROWCOUNT;

	
END

CREATE  PROCEDURE [dbo].[usp_InsertPolicy_CustMapping]
(
	@PolicyId BIGINT,
    @CustomerId BIGINT,
	@AffectedRows INT OUTPUT
	
	)
AS

BEGIN SET NOCOUNT ON;
BEGIN UPDATE Policy SET Fuel = @Fuel, VehicleSegment = @VECHILE_SEGMENT, Premium = @Premium,bodily_injury_liability = @InjuryLiability, personal_injury_protection = @InjuryProtection,property_damage_liability = @ProperyLiability, collision = @Collision,comprehensive =@Comprehensive Where PolicyCode = @PolicyCode END
SELECT @@ROWCOUNT; END

